// Extract and validate input data from webhook
const webhookData = $input.first().json;
console.log('ðŸ” Raw webhook data:', JSON.stringify(webhookData, null, 2));

// Extract data from webhook body
const inputData = webhookData.body || webhookData;
console.log('ðŸ“‹ Extracted input data:', JSON.stringify(inputData, null, 2));

// Validate required fields
if (!inputData.session_id) {
  console.error('âŒ Missing required field: session_id');
  throw new Error('Missing required field: session_id');
}

// Prepare report generation data
const reportData = {
  sessionId: inputData.session_id,
  candidateId: inputData.candidate_id,
  jobDescriptionId: inputData.job_description_id,
  includeDetailedAnalysis: inputData.include_detailed_analysis || true,
  includeHiringRecommendation: inputData.include_hiring_recommendation || true,
  includeSkillGaps: inputData.include_skill_gaps || true,
  reportFormat: inputData.report_format || 'comprehensive',
  timestamp: new Date().toISOString()
};

console.log('ðŸ“Š Report generation data prepared:', {
  sessionId: reportData.sessionId,
  candidateId: reportData.candidateId,
  jobDescriptionId: reportData.jobDescriptionId,
  reportFormat: reportData.reportFormat
});

return [{
  json: reportData
}];

// Consolidate all exam data for report generation
const sessionData = $('Get Exam Session').first().json;
const resultsData = $('Get Exam Results').first().json;
const responsesData = $('Get Exam Responses').all();
const candidateData = $('Get Candidate').first().json;
const jobData = $('Get Job Description').first().json;
const questionsData = $('Get Exam Questions').all();

console.log('ðŸ“Š Consolidating exam data for report generation...');
console.log('Session data:', sessionData);
console.log('Results data:', resultsData);
console.log('Responses count:', responsesData.length);
console.log('Candidate data:', candidateData);
console.log('Job data:', jobData);
console.log('Questions count:', questionsData.length);

// Create question lookup map
const questionMap = new Map();
questionsData.forEach(q => {
  questionMap.set(q.json.id, q.json);
});

// Process responses with question details
const processedResponses = responsesData.map(response => {
  const question = questionMap.get(response.json.question_id);
  return {
    ...response.json,
    question: question
  };
});

// Separate MCQ and text responses
const mcqResponses = processedResponses.filter(r => r.question?.question_type === 'mcq');
const textResponses = processedResponses.filter(r => r.question?.question_type === 'text');

// Calculate MCQ performance
const mcqStats = {
  totalQuestions: mcqResponses.length,
  correctAnswers: mcqResponses.filter(r => r.is_correct).length,
  totalScore: mcqResponses.reduce((sum, r) => sum + (r.points_earned || 0), 0),
  maxScore: mcqResponses.reduce((sum, r) => sum + (r.question?.points || 0), 0),
  averageTime: mcqResponses.reduce((sum, r) => sum + (r.time_taken_seconds || 0), 0) / mcqResponses.length
};

// Calculate text performance
const textStats = {
  totalQuestions: textResponses.length,
  totalScore: textResponses.reduce((sum, r) => sum + (r.points_earned || 0), 0),
  maxScore: textResponses.reduce((sum, r) => sum + (r.question?.points || 0), 0),
  averageTime: textResponses.reduce((sum, r) => sum + (r.time_taken_seconds || 0), 0) / textResponses.length
};

// Calculate overall performance
const overallStats = {
  totalQuestions: processedResponses.length,
  correctAnswers: processedResponses.filter(r => r.is_correct).length,
  totalScore: resultsData?.total_score || 0,
  maxScore: resultsData?.max_score || 0,
  percentage: resultsData?.percentage || 0,
  timeTaken: resultsData?.time_taken_minutes || 0,
  evaluationStatus: resultsData?.evaluation_status || 'pending'
};

// Prepare consolidated data
const consolidatedData = {
  sessionId: sessionData.id,
  candidate: candidateData,
  jobDescription: jobData,
  examSession: sessionData,
  examResults: resultsData,
  responses: processedResponses,
  mcqResponses: mcqResponses,
  textResponses: textResponses,
  mcqStats: mcqStats,
  textStats: textStats,
  overallStats: overallStats,
  reportMetadata: {
    generatedAt: new Date().toISOString(),
    reportFormat: $json.reportFormat,
    includeDetailedAnalysis: $json.includeDetailedAnalysis,
    includeHiringRecommendation: $json.includeHiringRecommendation,
    includeSkillGaps: $json.includeSkillGaps
  }
};

console.log('âœ… Consolidated data prepared:', {
  sessionId: consolidatedData.sessionId,
  candidateName: consolidatedData.candidate?.name,
  jobTitle: consolidatedData.jobDescription?.title,
  totalQuestions: consolidatedData.overallStats.totalQuestions,
  overallScore: consolidatedData.overallStats.totalScore,
  percentage: consolidatedData.overallStats.percentage
});

return [{
  json: consolidatedData
}];

