// Process AI-generated report and prepare for storage
const aiResponse = $input.first().json;
const consolidatedData = $('Consolidate Exam Data').first().json;

console.log('ðŸ“Š Processing AI-generated report...');
console.log('AI Response:', JSON.stringify(aiResponse, null, 2));

// Parse AI response
let reportData;
try {
  // Try to parse as JSON first
  if (typeof aiResponse === 'string') {
    reportData = JSON.parse(aiResponse);
  } else if (aiResponse.choices && aiResponse.choices[0] && aiResponse.choices[0].message) {
    // OpenAI API response format
    const content = aiResponse.choices[0].message.content;
    reportData = JSON.parse(content);
  } else {
    reportData = aiResponse;
  }
} catch (error) {
  console.error('âŒ Error parsing AI response:', error);
  // Fallback: create basic report structure
  reportData = {
    executive_summary: {
      overall_performance: 'average',
      key_strengths: ['Basic technical knowledge'],
      main_concerns: ['Limited experience'],
      hiring_recommendation: 'conditional_hire',
      confidence_level: 0.5,
      summary_text: 'Candidate shows basic competency with room for improvement.'
    },
    performance_analysis: {
      mcq_performance: {
        score: consolidatedData.mcqStats.totalScore,
        max_score: consolidatedData.mcqStats.maxScore,
        percentage: consolidatedData.mcqStats.totalQuestions > 0 ? Math.round((consolidatedData.mcqStats.totalScore / consolidatedData.mcqStats.maxScore) * 100) : 0,
        strengths: ['Basic knowledge'],
        weaknesses: ['Limited depth'],
        analysis: 'Basic performance with room for improvement'
      },
      text_performance: {
        score: consolidatedData.textStats.totalScore,
        max_score: consolidatedData.textStats.maxScore,
        percentage: consolidatedData.textStats.totalQuestions > 0 ? Math.round((consolidatedData.textStats.totalScore / consolidatedData.textStats.maxScore) * 100) : 0,
        strengths: ['Basic understanding'],
        weaknesses: ['Limited detail'],
        analysis: 'Basic text performance'
      },
      overall_performance: {
        score: consolidatedData.overallStats.totalScore,
        max_score: consolidatedData.overallStats.maxScore,
        percentage: consolidatedData.overallStats.percentage,
        time_efficiency: 'average',
        consistency: 'medium',
        analysis: 'Overall average performance'
      }
    },
    question_analysis: consolidatedData.responses.map((r, index) => ({
      question_id: r.question_id,
      question_type: r.question?.question_type || 'unknown',
      category: r.question?.question_category || 'unknown',
      difficulty: r.question?.difficulty_level || 'medium',
      points_earned: r.points_earned || 0,
      max_points: r.question?.points || 1,
      is_correct: r.is_correct || false,
      time_taken_seconds: r.time_taken_seconds || 0,
      performance_rating: r.is_correct ? 'good' : 'poor',
      feedback: r.is_correct ? 'Correct answer' : 'Incorrect answer',
      strengths: r.is_correct ? ['Correct understanding'] : [],
      improvements: r.is_correct ? [] : ['Review topic']
    })),
    skill_gap_analysis: {
      critical_gaps: [],
      important_gaps: [],
      nice_to_have_gaps: []
    },
    strengths: ['Basic technical knowledge'],
    weaknesses: ['Limited experience'],
    hiring_recommendation: {
      recommendation: 'conditional_hire',
      confidence: 0.5,
      reasoning: 'Basic competency with potential for growth',
      conditions: ['Additional training required'],
      interview_focus: ['Technical depth assessment'],
      salary_recommendation: {
        range: 'Entry level',
        reasoning: 'Based on current skill level'
      }
    },
    development_suggestions: {
      immediate: ['Review exam topics'],
      short_term: ['Practice technical skills'],
      long_term: ['Gain more experience'],
      resources: ['Online courses', 'Practice projects']
    },
    interview_guidance: {
      focus_areas: ['Technical skills'],
      questions_to_ask: ['Experience with relevant technologies'],
      red_flags: ['Lack of basic knowledge'],
      green_flags: ['Willingness to learn']
    },
    report_metadata: {
      generated_at: new Date().toISOString(),
      report_version: '1.0',
      ai_confidence: 0.5,
      data_completeness: 'complete'
    }
  };
}

// Add raw data for reference
const finalReport = {
  ...reportData,
  raw_data: {
    session_id: consolidatedData.sessionId,
    candidate_id: consolidatedData.candidate?.id,
    job_description_id: consolidatedData.jobDescription?.id,
    exam_results: consolidatedData.examResults,
    responses: consolidatedData.responses,
    mcq_stats: consolidatedData.mcqStats,
    text_stats: consolidatedData.textStats,
    overall_stats: consolidatedData.overallStats
  },
  processing_metadata: {
    generated_at: new Date().toISOString(),
    processing_time_seconds: 0,
    data_sources: ['exam_sessions', 'exam_results', 'exam_responses', 'candidates', 'job_descriptions', 'exam_questions'],
    ai_model: 'gpt-4',
    report_format: consolidatedData.reportMetadata.reportFormat
  }
};

console.log('âœ… Final report prepared:', {
  sessionId: finalReport.raw_data.session_id,
  candidateName: consolidatedData.candidate?.name,
  hiringRecommendation: finalReport.hiring_recommendation.recommendation,
  confidence: finalReport.hiring_recommendation.confidence
});

return [{
  json: finalReport
}];

